Sheet sh = wb.getSheet("Data")
def rowsize = sh.getRows().toInteger()
def colsize =  sh.getColumns().toInteger()
log.info "No of rows = " + rowsize-1
log.info "No of columns = " +colsize-1

def names = []
def valuesTC1 = []
def valuesTC2 = []
for(i=1;i<rowsize;i++){
	name = sh.getCell(0,i).getContents()
	names.add(name)
}

for(i=1;i<rowsize;i++){
	value = sh.getCell(1,i).getContents()
	valuesTC1.add(value)
}

for(i=1;i<rowsize;i++){
	value = sh.getCell(2,i).getContents()
	valuesTC2.add(value)
}



testRunner.testCase.testSuite.testCases["End to End Testing - Data Driven"]
	.getTestStepByName("Get Data from excel").disabled = true

//Execute TC1
for(i=0;i<rowsize-1;i++){
	name = names[i]
	context.testCase.testSuite.testCases["End to End Testing - Data Driven"].getTestStepByName("Properties").setPropertyValue("$name",valuesTC1[i])
}

def tc = testRunner.testCase.testSuite.testCases["End to End Testing - Data Driven"]

def runner = tc.run(null,false)
log.info "Number of test steps executed in TC1 = " + runner.results.size()


//Execute TC2
